import YeomanGenerator from 'yeoman-generator';
import options from '../options';

export default class extends YeomanGenerator {
  constructor(...args) {
    super(...args);

    for (const [name, value] of Object.entries(options)) {
      this.option(name, value);
    }
  }
}

import chalk from 'chalk';
import BaseGenerator from '../base-generator';

export default class extends BaseGenerator {
  writing() {
    if (this.options.githubTemplates) {
      const templatesToCopy = [
        { templatePath: '_github', destinationPath: '.github' },
        { templatePath: 'other', destinationPath: 'other' },
        { templatePath: 'contributing.md', destinationPath: 'contributing.md' },
      ];

      templatesToCopy.forEach(({ templatePath, destinationPath }) =>
        this.fs.copyTpl(
          this.templatePath(templatePath),
          this.destinationPath(destinationPath),
          this.options
        )
      );
    }
  }

  async install() {
    if (this.options.createGithubRepository) {
      await this._createGithubRepository();
    }
  }

  async _createGithubRepository() {
    this.log('Creating github repository...');

    const {
      data: { html_url: url },
    } = await this.options.github.createRepository({
      name: this.options.projectName,
      description: this.options.description,
    });

    this.githubRepositoryUrl = url;

    this.log(this.githubRepositoryUrl);
    this.log('\n');
  }

  end() {
    if (this.options.createGithubRepository) {
      this.log(
        chalk`  Check out your new Github repository: {underline.cyan ${this.githubRepositoryUrl}}`
      );
    }
  }
}

import path from 'path';
import chalk from 'chalk';
import findUp from 'find-up';
import makeDir from 'make-dir';

import Commander from '../../lib/commander';
import BaseGenerator from '../base-generator';

export default class extends BaseGenerator {
  constructor(...args) {
    super(...args);

    this.commander = new Commander({
      spawnSync: this.spawnCommandSync.bind(this),
      log: this.log.bind(this),
    });
  }
  /*
    Run Loop
   */

  configuring() {
    if (path.basename(this.destinationRoot()) !== this.options.projectName) {
      return makeDir(this.options.projectName).then(path => {
        this.destinationRoot(path);
        this.log(chalk`\nGenerating a new project in {green ${path}}\n`);
      });
    }
  }

  writing() {
    const templatesToCopy = [
      { templatePath: '_babelrc', destinationPath: '.babelrc' },
      { templatePath: '_editorconfig', destinationPath: '.editorconfig' },
      { templatePath: '_eslintignore', destinationPath: '.eslintignore' },
      { templatePath: '_eslintrc', destinationPath: '.eslintrc' },
      { templatePath: '_gitattributes', destinationPath: '.gitattributes' },
      { templatePath: '_gitignore', destinationPath: '.gitignore' },
      { templatePath: '_npmignore', destinationPath: '.npmignore' },
      { templatePath: '_package.json', destinationPath: 'package.json' },
      { templatePath: 'license', destinationPath: 'license' },
      { templatePath: 'readme.md', destinationPath: 'readme.md' },
      { templatePath: 'src', destinationPath: 'src' },
      {
        templatePath: 'config/jest.config.js',
        destinationPath: 'config/jest.config.js',
      },
      {
        templatePath: 'config/webpack.config.js',
        destinationPath: 'config/webpack.config.js',
      },
    ];

    if (this.options.esdoc) {
      templatesToCopy.push({
        templatePath: 'config/esdoc.config.js',
        destinationPath: 'config/esdoc.config.js',
      });
    }

    if (this.options.npmDeploy && this.options.semanticRelease) {
      templatesToCopy.push({
        templatePath: 'config/commitlint.config.js',
        destinationPath: 'config/commitlint.config.js',
      });
    }

    templatesToCopy.forEach(({ templatePath, destinationPath }) =>
      this.fs.copyTpl(
        this.templatePath(templatePath),
        this.destinationPath(destinationPath),
        this.options
      )
    );
  }

  install() {
    this._installNpmDeps();
  }

  async end() {
    const { repository } = this.options;
    await this._removeYoRc();

    this._createGit();

    this.log('\n\n\n');
    this.log(chalk`{bold.yellow Thank you for using generator-node-mdl!}`);
    this.log('\n\n\n');
    this.log(chalk`{bold.underline What's next:}\n`);

    this.log(chalk`  Push your changes to git: {gray git push origin master}`);
    this.log(
      chalk`  Read your project contributing guide: {underline.cyan https://github.com/${repository}/blob/master/contributing.md}`
    );
    this.log(
      chalk`  Enable greenkeeper for your project by visiting: {underline.cyan https://account.greenkeeper.io/}`
    );
  }

  /*
    Private Methods
   */

  _installNpmDeps() {
    const { hasYarn } = this.options;

    return this.installDependencies({
      bower: false,
      npm: !hasYarn,
      yarn: hasYarn,
      skipMessage: true,
    });
  }

  _createGit() {
    const { repository } = this.options;

    this.log('Initiating git:');

    this.commander.run([
      'git init --quiet',
      `git remote add origin git@github.com:${repository}.git`,
      'git add .',
      [
        'git',
        'commit',
        '-m',
        'chore(init): generated by generator-node-mdl ðŸ”¥',
        '--quiet',
      ],
    ]);
  }

  async _removeYoRc() {
    const yoRC = await findUp('.yo-rc.json');

    if (yoRC) {
      this.fs.delete(yoRC);

      await new Promise((resolve, reject) => {
        this.fs.commit(() => resolve());
      });
    }
  }
}

import chalk from 'chalk';

import Commander from '../../lib/commander';
import BaseGenerator from '../base-generator';

export default class extends BaseGenerator {
  constructor(...args) {
    super(...args);

    this.commander = new Commander({
      spawnSync: this.spawnCommandSync.bind(this),
    });
  }

  writing() {
    this.fs.copyTpl(
      this.templatePath('_travis.yml'),
      this.destinationPath('.travis.yml'),
      this.options
    );
  }

  async install() {
    if (process.env.NODE_ENV === 'test') return;

    this.log('Installing TravisCI...');

    const { npmDeploy, semanticRelease } = this.options;

    this._installTravis();

    if (npmDeploy) {
      this._installTravisNpmToken();

      if (semanticRelease) {
        this._installTravisGithubToken();
      }
    }

    this.log('\n');
  }

  end() {
    const { repository } = this.options;

    this.log(
      chalk`  Visit your TravisCI url: {underline.cyan https://travis-ci.org/${repository}}`
    );
  }

  _installTravis() {
    const { repository, githubToken } = this.options;

    this.commander.run([
      'gem install travis --no-document --quiet',
      `travis login --github-token ${githubToken}`,
      `travis enable -r ${repository}`,
    ]);
  }

  _installTravisNpmToken() {
    const { repository, npmToken } = this.options;

    this.commander.run(`travis env set NPM_TOKEN ${npmToken} -r ${repository}`);
  }

  _installTravisGithubToken() {
    const { repository, githubToken } = this.options;

    this.commander.run(
      `travis env set GH_TOKEN ${githubToken} -r ${repository}`
    );
  }
}

import YeomanGenerator from 'yeoman-generator';
import chalk from 'chalk';
import commandExists from 'command-exists';

import './generators/project';
import './generators/github';
import './generators/travis';

import Prompter from './prompter';

export default class NodeMdlGenerator extends YeomanGenerator {
  constructor(...args) {
    super(...args);

    this.prompter = new Prompter(this);
  }

  async prompting() {
    this.props = await this.prompter.prompt();
  }

  configuring() {
    this.props.hasYarn = commandExists.sync('yarn');
    this.props.runScriptCommand = this.props.hasYarn ? 'yarn' : 'npm run';

    this.composeWith(require.resolve('./generators/project'), this.props);
    this.composeWith(require.resolve('./generators/github'), this.props);

    if (this.props.travisCI) {
      this.composeWith(require.resolve('./generators/travis'), this.props);
    }
  }

  writing() {
    this.log('\n\n\n');
    this.log(chalk`{underline.bold Writing files to disk:}\n`);
  }

  install() {
    this.log('\n\n\n');
    this.log(chalk`{underline.bold Installing:}\n`);
  }

  end() {
    this.log('\n\n\n');
    this.log(chalk`{underline.bold Finishing:}\n`);
  }
}

import path from 'path';
import assert from 'yeoman-assert';
import helpers from 'yeoman-test';

import Generator from './index';
import Github from './lib/github';

jest.mock('./lib/github');
jest.mock('./lib/npm');

Github.use2fa = false;

const runAppGenerator = () => helpers.run(Generator);

beforeEach(() => jest.clearAllMocks());

const requiredPrompts = {
  githubUsername: 'github-username',
};

test('destinationRoot', () => {
  return runAppGenerator()
    .withPrompts({ ...requiredPrompts, projectName: 'temp' })
    .then(() => {
      assert.equal(path.basename(process.cwd()), 'temp');
    });
});

test('default files', () => {
  return runAppGenerator()
    .withPrompts(requiredPrompts)
    .then(() => {
      assert.file([
        '.git',
        '.babelrc',
        '.editorconfig',
        '.eslintignore',
        '.eslintrc',
        '.gitattributes',
        '.gitignore',
        '.npmignore',
        'license',
        'package.json',
        'readme.md',
        'config/jest.config.js',
        'config/webpack.config.js',
        'src/index.js',
        'src/index.test.js',
        // esdoc
        'config/esdoc.config.js',
        // githubTemplates
        'contributing.md',
        '.github/issue_template.md',
        '.github/pull_request_template.md',
        'other/code_of_conduct.md',
        'other/examples.md',
        'other/roadmap.md',
        // travisCI
        '.travis.yml',
        // semanticRelease
